{"output":"import { DateTime } from 'luxon'

export function getHourlySlots(
  startUtc: string,
  hours: number,
  stepMinutes: number = 60
): DateTime[] {
  const slots: DateTime[] = []
  const start = DateTime.fromISO(startUtc, { zone: 'utc' })
  const totalMinutes = hours * 60
  const steps = totalMinutes / stepMinutes

  for (let i = 0; i < steps; i++) {
    slots.push(start.plus({ minutes: i * stepMinutes }))
  }

  return slots
}

export function formatTimeForZone(
  utcTime: DateTime,
  tzId: string,
  is24Hour: boolean = true
): string {
  const local = utcTime.setZone(tzId)
  return local.toFormat(is24Hour ? 'HH:mm' : 'h:mm a')
}

export function getTimeColor(hour: number): string {
  if (hour >= 5 && hour < 8) return 'earlymorning'  // 早朝
  if (hour >= 8 && hour < 17) return 'daytime'      // 日中
  if (hour >= 17 && hour < 22) return 'evening'     // 夜間
  return 'midnight'                                   // 深夜
}

export function getTimeBackgroundClass(hour: number): string {
  // 深夜 (0-5時、22-24時)
  if (hour >= 0 && hour < 5 || hour >= 22) {
    return 'time-midnight'
  }
  // 早朝 (5-8時)
  if (hour >= 5 && hour < 8) {
    return 'time-earlymorning'
  }
  // 日中 (8-17時)
  if (hour >= 8 && hour < 17) {
    return 'time-daytime'
  }
  // 夜間 (17-22時)
  if (hour >= 17 && hour < 22) {
    return 'time-evening'
  }
  return 'bg-white dark:bg-gray-900'
}

export function isToday(date: DateTime): boolean {
  return date.hasSame(DateTime.now(), 'day')
}

export function isNow(date: DateTime, stepMinutes: number = 60): boolean {
  const now = DateTime.now()
  const diff = Math.abs(date.diff(now, 'minutes').minutes)
  return diff < stepMinutes / 2
}

export function formatDayHeader(date: DateTime): string {
  if (isToday(date)) {
    return `今日 ${date.toFormat('M/d')}`
  }
  return date.setLocale('ja').toFormat('M/d (EEE)')
}
