    if (cities.some(city => city.tzId === homeTimeZone)) return
    // データセットから同一TZの代表都市を探索
    fetch('/data/city-timezones.json')
      .then(res => res.json())
      .then((all: any[]) => {
        const found = all.find((c) => c.tzId === homeTimeZone)
        const homeCityData = found ? {
          id: found.id,
          label: found.label,
          labelEn: found.labelEn,
          country: found.country,
          countryEn: found.countryEn,
          tzId: found.tzId,
          aliases: found.aliases,
        } : {
          id: 'home',
          label: homeTimeZone.split('/').pop() || '現在地',
          labelEn: 'Current Location',
          country: '',
          countryEn: '',
          tzId: homeTimeZone,
          aliases: ['現在地', 'Current Location', 'Home']
        }
        setHomeCity(homeCityData)
      })
      .catch(() => {
        const fallback = {
          id: 'home',
          label: homeTimeZone.split('/').pop() || '現在地',
          labelEn: 'Current Location',
          country: '',
          countryEn: '',
          tzId: homeTimeZone,
          aliases: ['現在地', 'Current Location', 'Home']
        }
        setHomeCity(fallback)
      })
  }, [cities])

  const handleDragStart = (e: React.DragEvent, index: number) => {
    setDraggedIndex(index)
    e.dataTransfer.effectAllowed = 'move'
  }

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault()
    e.dataTransfer.dropEffect = 'move'
  }

  const handleDrop = (e: React.DragEvent, dropIndex: number) => {
    e.preventDefault()

    if (draggedIndex === null || draggedIndex === dropIndex) {
      return
    }

    const newCities = [...cities]
    const draggedCity = newCities[draggedIndex]

    // Remove from old position
    newCities.splice(draggedIndex, 1)

    // Insert at new position
    newCities.splice(dropIndex, 0, draggedCity)

    reorderCities(newCities)
    setDraggedIndex(null)
  }

  return (
    <div className="flex flex-col h-full">
      <div className="p-4 border-b">
        <h2 className="text-lg font-semibold mb-3">都市リスト</h2>
        <button 
          onClick={() => {
            console.log('Add city button clicked')
            setShowSearch(true)
          }}
          className="w-full flex items-center justify-center gap-2 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors"
        >
          <Plus className="w-4 h-4" />
          都市を追加
        </button>
      </div>

      <div className="flex-1 overflow-y-auto p-4 space-y-2">
        {homeCity && !cities.some(c => c.id === 'home') && (
          <div className="mb-4">
            <div className="flex items-center gap-2 text-sm text-gray-600 dark:text-gray-400 mb-2">
              <MapPin className="w-4 h-4" />
              <span>現在地のタイムゾーン</span>
            </div>
              <div className="bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-800 rounded-lg p-3">
                <div className="flex items-center justify-between">
                  <div>
                    <div className="font-medium">{homeCity.label}（{homeCity.tzId}）</div>
                    <div className="text-sm text-gray-600 dark:text-gray-400">
                      {DateTime.now().setZone(homeCity.tzId).toFormat('HH:mm:ss')}
                    </div>
                  </div>
                <button
                  onClick={() => addCity(homeCity)}
                  className="text-sm px-3 py-1 bg-blue-500 text-white rounded hover:bg-blue-600"
                >
                  追加
                </button>
              </div>
            </div>
          </div>
        )}

        {cities.length === 0 ? (
          <p className="text-gray-500 text-center py-8">
            都市を追加してください
          </p>
        ) : (
          cities.map((city, index) => (
            <div
              key={city.id}
              draggable
              onDragStart={(e) => handleDragStart(e, index)}
              onDragOver={handleDragOver}
              onDrop={(e) => handleDrop(e, index)}
            >
              <CityItem
                city={city}
                onRemove={() => removeCity(city.id)}
                isDragging={draggedIndex === index}
              />
            </div>
          ))
        )}
      </div>

      {showSearch && (
        <CitySearch
          onSelect={addCity}
          onClose={() => setShowSearch(false)}
        />
      )}
    </div>
  )
}
