import { useEffect } from 'react'
import { DateTime } from 'luxon'
import { useCities, City } from '@/store/useCities'
import { useView } from '@/store/useView'
import { useSelection } from '@/store/useSelection'

export function QueryLoader() {
  const { cities, addCity } = useCities()
  const { range, settings, setRange } = useView()

  useEffect(() => {
    const url = new URL(window.location.href)
    const fromParam = url.searchParams.get('from')
    const toParam = url.searchParams.get('to')
    const cityParam = url.searchParams.get('cities')

    // Load cities if requested
    async function ensureCities() {
      if (!cityParam) return
      const ids = cityParam.split(',').map((s) => s.trim()).filter(Boolean)
      if (ids.length === 0) return
      try {
        const res = await fetch('/data/city-timezones.json')
        const all: City[] = await res.json()
        const missing = ids.filter((id) => !cities.some((c) => c.id === id))
        missing.forEach((id) => {
          const found = all.find((c) => c.id === id)
          if (found) addCity(found)
        })
      } catch (e) {
        console.error('Failed to load cities for query:', e)
      }
    }

    ensureCities()

    // Apply selection and range once cities are available (for correct tz alignment)
    const applySelection = () => {
      if (!fromParam || !toParam) return
      const from = DateTime.fromISO(fromParam, { zone: 'utc' })
      const to = DateTime.fromISO(toParam, { zone: 'utc' })
      if (!from.isValid || !to.isValid) return

      const tz = cities.length > 0 ? cities[0].tzId : Intl.DateTimeFormat().resolvedOptions().timeZone
      const localMidnight = from.setZone(tz).startOf('day')
      const startDateUtc = localMidnight.toUTC()
      // set view range start aligned to selected day
      setRange({ ...range, fromUtc: startDateUtc.toISO()!, days: Math.max(1, Math.ceil(to.diff(from, 'hours').hours / 24)) })

      const step = settings.stepMinutes
      const startIdx = Math.max(0, Math.floor(from.diff(startDateUtc, 'minutes').minutes / step))
      const endIdx = Math.max(startIdx, Math.ceil(to.diff(startDateUtc, 'minutes').minutes / step) - 1)
      useSelection.getState().setRange(startIdx, endIdx)
    }

    // First attempt now, and again after a tick in case cities load later
    applySelection()
    const t = setTimeout(applySelection, 300)
    return () => clearTimeout(t)
  }, [cities])

  return null
}
