import { useEffect, useRef, useState } from 'react'

interface AutoFitProps {
  children: React.ReactNode
  className?: string
  minScale?: number
  maxScale?: number
}

export function AutoFit({ children, className, minScale = 0.6, maxScale = 1 }: AutoFitProps) {
  const containerRef = useRef<HTMLDivElement | null>(null)
  const contentRef = useRef<HTMLDivElement | null>(null)
  const [scale, setScale] = useState(1)

  useEffect(() => {
    const container = containerRef.current
    const content = contentRef.current
    if (!container || !content) return

    const compute = () => {
      const cw = container.clientWidth || 1
      const ch = container.clientHeight || 1
      // Use scroll sizes to get natural content size
      const tw = content.scrollWidth || 1
      const th = content.scrollHeight || 1
      const sx = cw / tw
      const sy = ch / th
      const next = Math.max(minScale, Math.min(maxScale, Math.min(sx, sy)))
      setScale(next)
    }

    compute()

    let ro: ResizeObserver | null = null
    if (typeof ResizeObserver !== 'undefined') {
      ro = new ResizeObserver(() => compute())
      ro.observe(container)
      ro.observe(content)
    } else {
      const onResize = () => compute()
      window.addEventListener('resize', onResize)
      return () => window.removeEventListener('resize', onResize)
    }
    return () => {
      if (ro) {
        ro.disconnect()
      }
    }
  }, [minScale, maxScale])

  return (
    <div ref={containerRef} className={className}>
      <div
        ref={contentRef}
        style={{ transform: `scale(${scale})`, transformOrigin: 'center center', display: 'inline-block' }}
      >
        {children}
      </div>
    </div>
  )
}

