import { DateTime } from 'luxon'
import { useCities } from '@/store/useCities'
import { useView } from '@/store/useView'
import { BlockItem } from '@/store/useBlocks'
import { getTimeBackgroundClass, getHourlySlots } from '@/lib/time/utils'
import { AutoFit } from '@/components/ui/AutoFit'

interface Props {
  block: BlockItem
  onChangeRange: (range: BlockItem['range']) => void
  onChangeSelection: (sel: BlockItem['selection']) => void
  onRemove?: () => void
}

export function CandidateBlock({ block, onChangeRange, onChangeSelection, onRemove }: Props) {
  const { cities } = useCities()
  const { settings } = useView()

  const startDate = DateTime.fromISO(block.range.fromUtc, { zone: 'utc' })
  const firstCityTz = cities.length > 0 ? cities[0].tzId : Intl.DateTimeFormat().resolvedOptions().timeZone
  const localMidnight = startDate.setZone(firstCityTz).startOf('day')
  const step = settings.stepMinutes
