:root {
  --bg: #FFF7F5;
  --surface: #FCEFEA;
  --text: #2E2323;
  --primary: #D68479; /* ブラッシュ */
  --accent: #E8C07D;  /* サンド */
  --label-w: 120px;   /* left label column width */

  --text-muted: color-mix(in oklab, var(--text), white 45%);
