}
.app-header h1 { margin: 0; font-size: 20px; letter-spacing: 0.5px; }

.app-main {
  display: grid;
  grid-template-columns: 300px 1fr 280px;
  gap: 16px;
  padding: 16px;
}

@media (max-width: 1100px) {
  .app-main { grid-template-columns: 1fr; }
  .panel-right { order: 3; }
  .panel-left { order: 2; }
