  color: var(--text);
  padding: 8px 12px;
  border-radius: 10px;
  cursor: pointer;
  transition: transform .06s ease, background .2s ease, box-shadow .2s ease;
}
.btn:hover { transform: translateY(-1px); box-shadow: 0 4px 12px rgba(0,0,0,.08); }
.btn:active { transform: translateY(0); }
.btn-primary { background: var(--primary); color: #fff; border-color: color-mix(in oklab, var(--primary), black 10%); }
.btn-primary:hover { background: color-mix(in oklab, var(--primary), black 10%); }
.btn-accent { background: var(--accent); color: #2e2323; border-color: color-mix(in oklab, var(--accent), black 10%); }
.btn-surface { background: var(--surface); }

.icon-btn {
