.reco .reco-actions { display: flex; gap: 8px; }

.city-list { list-style: none; padding: 0; margin: 0; display: flex; flex-direction: column; gap: 8px; }
.city-item {
  display: grid; grid-template-columns: 1fr auto; gap: 8px; align-items: center;
  background: #fff; border: 1px solid var(--outline); border-radius: 12px; padding: 10px; cursor: grab;
}
.city-item:active { cursor: grabbing; }
.city-title { font-weight: 600; }
.city-sub { color: var(--text-muted); font-size: 12px; }
.city-actions { display: flex; gap: 6px; align-items: center; }
.pill { font-size: 11px; padding: 3px 8px; border-radius: 999px; background: var(--surface); border: 1px solid var(--outline); color: var(--text-muted); }

/* Right panel */
.setting { display: grid; gap: 10px; }
.segmented { display: inline-flex; background: white; border: 1px solid var(--outline); padding: 4px; border-radius: 12px; gap: 4px; }
.seg {
  padding: 6px 10px; border-radius: 10px; border: 1px solid transparent; cursor: pointer; background: transparent; color: var(--text);
}
.seg[aria-pressed="true"] { background: var(--primary); color: white; border-color: color-mix(in oklab, var(--primary), black 10%); }

