.round-layer .pill { position: absolute; top: 4px; bottom: 4px; border-radius: 14px; background: color-mix(in oklab, var(--accent), white 82%); z-index: 0; }
.round-layer .night { position: absolute; top: 4px; bottom: 4px; background: color-mix(in oklab, var(--text), white 40%); opacity: 0.35; border-radius: 0; z-index: 1; }
.round-layer .date-chip { position: absolute; transform: translate(-50%, -50%); top: 50%; padding: 0; border-radius: 0; background: transparent; border: none; font-size: clamp(7px, calc(var(--hour-width, 80px) * 0.28), 12px); line-height: 1.05; color: #fff; text-align: center; white-space: nowrap; box-shadow: none; z-index: 2; }
.round-layer .date-chip .d1 { display: block; }
.round-layer .date-chip .d2 { display: block; font-weight: 600; color: #fff; }

/* Now line */
.now-line { position: absolute; top: 0; bottom: 0; width: 2px; background: #e25544; box-shadow: 0 0 0 1px rgba(226,85,68,.2); pointer-events: none; }

/* Outputs */
.outputs { font-family: ui-monospace, SFMono-Regular, Menlo, Consolas, "Liberation Mono", monospace; background: #fff; border: 1px solid var(--outline); border-radius: 10px; padding: 8px; white-space: pre-wrap; }
.outputs-card details { border: 1px solid var(--outline); border-radius: 10px; background: #fff; padding: 6px 8px; }
.outputs-card summary { cursor: pointer; list-style: none; font-weight: 600; color: var(--text); }
.outputs-card summary::-webkit-details-marker { display: none; }
.outputs-card pre { margin: 8px 0 0; }

/* Hover guide line */
.hover-line { position: absolute; top: 0; bottom: 0; width: 1px; background: transparent; border-left: 1px dashed color-mix(in oklab, var(--text), white 60%); pointer-events: none; }

/* Consensus overlay across all rows (meeting friendliness) */
.consensus { position: absolute; inset: 0; pointer-events: none; }
.consensus .cs-slot { position: absolute; top: 0; bottom: 0; background: transparent; opacity: 0; border-radius: 4px; }

/* Per-row hour labels and vertical lines */
.row-hours { position: absolute; inset: 0; pointer-events: none; z-index: 3; }
.row-hours .h { position: absolute; top: 50%; transform: translate(-50%, -50%); font-size: 14px; font-weight: 600; color: color-mix(in oklab, var(--text), white 45%); font-variant-numeric: tabular-nums; }
.row-hours .h.night { color: #fff; text-shadow: 0 1px 2px rgba(0,0,0,.35); }
.row-hours .h.date { font-size: clamp(6px, calc(var(--hour-width, 80px) * 0.22), 11px); font-weight: 700; color: var(--text); line-height: 1.05; text-align: center; width: calc(var(--hour-width, 80px) - 6px); white-space: normal; background: transparent; border: none; padding: 0; box-shadow: none; }
.vlines { position: absolute; inset: 0; pointer-events: none; }
.vlines .v { position: absolute; top: 0; bottom: 0; border-left: none; }
.vlines .v-day { border-left: 2px solid color-mix(in oklab, var(--text), white 50%); }
